import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import logo from '../images/infographic.png';

//#80D0D0
class Navbar extends Component {

    constructor(props){
        super(props)
        this.state={
            ShowSearchBar:false,
        }
    }


    refreshNavBar=()=> {
        window.location.reload(false)
     }
    refreshSearchState=()=>{
        this.setState({ShowSearchBar:true})
    }
    searchBar=()=>{
        if(this.state.ShowSearchBar){
            return(
                <form class="form-inline d-flex justify-content-center md-form form-sm active-pink active-pink-2 mt-2">
                    <input className="form-control form-control-sm ml-3 w-75" type="text" placeholder="Search"
                            aria-label="Search"/>
                </form>  
            )
        }
    }


    render(){
        return (
            <>
            <div className="jumbotron" style={{marginBottom:0}}>
            </div>
            <nav className="navbar navbar-expand-md navbar-default sticky" style={{marginBottom:0,minHeight:80}}>

                <button type="button" className="navbar-toggle iconStyle" data-toggle="collapse" data-target="#collapse_target">
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                </button>

                <a className="navbar-brand" href="/"><img src={logo} className="logo" /></a>                       

                <span id="onlineSchool" className="navbar-text">OnlineSchool</span>



                <span onClick={this.refreshSearchState} className="glyphicon glyphicon-search navbar-brand" id="searchIcon"></span>
                {this.searchBar()}

                <div className="collapse navbar-collapse" id="collapse_target" style={{margin:0}}>  

                <ul className="nav navbar-nav navbar-right ul_nav" style={{ marginTop:10, marginRight:10}} >

                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink exact to="/">Accueil </NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink to="/Concours">Concours</NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink to="/Examens">Examens</NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink to="/contact">Nous contacter</NavLink>
                    </li>
                    <button onClick={this.deconnexion} className="button" style={{backgroundColor:"gray"}} >
                        <a className="navbar-brand" style={{color:'white',margin:"auto"}}>S'abonner</a>
                    </button>
                    <button onClick={this.deconnexion} className="button" style={{backgroundColor:"#660099",}} >
                        <a className="navbar-brand" style={{color:"white",margin:"auto"}}>Connexion</a>
                    </button>
                </ul> 
                </div>            
            </nav>
            </>
        );
    }
}

export default Navbar
