import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import logo from '../images/infographic.png';
import Modal from 'react-modal';
import {AuthLogin} from '../components/AuthLogin';
import {AuthSignUp} from '../components/AuthSignUp';

//#80D0D0
class Navbar extends Component {

    constructor(props){
        super(props)
        this.state={
            create_Account:false,
            log_in:false,
            modalVisible:false,
            isconnect:true,

        }
    }
    isconnect=()=>{
        if(this.state.isconnect){
            return(
                <>
                    <button className="button" type="button" data-toggle="dropdown" style={{backgroundColor:"#660099",}}>
                        <a className="navbar-brand" style={{color:'white'}}>Moi <span className="caret"></span></a>
                    </button>
                    <ul className="dropdown-menu ul_dropdown">
                        <li className="li_dropdown" activeClassName="main-nav">
                            <a style={{cursor:'default'}}><span style={styles.glyphiconDropdown} className="glyphicon glyphicon-user"></span>
                            Tchoffo Jean Aristote</a>
                        </li>
                        <li className="li_dropdown" activeClassName="main-nav">
                            <NavLink exact to="/parametres"><span style={styles.glyphiconDropdown} class="glyphicon glyphicon-cog"></span>
                             Mes paramètres </NavLink>
                        </li>
                        <li className="li_dropdown" activeClassName="main-nav">
                            <NavLink exact to="/notifications"><span style={styles.glyphiconDropdown} class="glyphicon glyphicon-cog"></span>
                             Mes paramètres </NavLink>
                        </li>
                        <li className="li_dropdown" activeClassName="main-nav">
                            <a exact to="/"><span style={styles.glyphiconDropdown} className="glyphicon glyphicon-off"></span>
                            Déconnexion </a>
                        </li>
                    </ul>
                </>
            )
        }else{
            return(
                <>
                    <button onClick={this.isCreateAccount} className="button" style={{backgroundColor:'rgba(0,0,0,0.8)'}} >
                        <a className="navbar-brand" style={{color:'white',margin:"auto"}}>S'abonner</a>
                    </button>
                    <button onClick={this.isLogIn} className="button" style={{backgroundColor:"#660099",}} >
                        <a className="navbar-brand" style={{color:"white",margin:"auto"}}>Connexion</a>
                    </button>
                </>
            )
        }
    }
    glyphiconSignUp=()=>{
        if(this.state.create_Account){
            return(
                <>
                <div style={{height:2,backgroundColor:'white',marginRight:50,marginLeft:50}}>                    
                </div>
                <div>
                    <span className="glyphicon glyphicon-menu-down"></span>
                </div>
                </>

            )
        }
    }
    glyphiconLogIn=()=>{
        if(this.state.log_in){
            return(
                <>
                <div style={{height:2,backgroundColor:'white',marginRight:50,marginLeft:50}}>                    
                </div>
                <div>
                    <span className="glyphicon glyphicon-menu-down"></span>
                </div>
                </>
            )
        }
    }
    customMargin=()=>{
        var margin="35%";
        var screenWidth= parseInt(window.innerWidth,10);
        if(screenWidth<=450){
            margin = "5%"
        }else{
            if(screenWidth<=600){
                margin="15%"
            }else{
                if(screenWidth<=800){
                    margin="20%"
                }
            }
        } 
        return margin
    }

    isLogIn=()=>{
         this.setState({
            log_in:true,
            create_Account:false,
            modalVisible:true,

        })
    } 

