import { NavLink } from 'react-router-dom';
import logo from '../images/infographic.png';
import Modal from 'react-modal'

//#80D0D0
class Navbar extends Component {

    constructor(props){
        super(props)
        this.state={
            create_Account:false,
            log_in:false,
            modalVisible:false,

        }
    }
    glyphiconSignUp=()=>{
        if(this.state.create_Account){
            return(
                <>
                <div style={{height:2,backgroundColor:'white',marginRight:50,marginLeft:50}}>                    
                </div>
                <div>
                    <span className="glyphicon glyphicon-menu-down"></span>
                </div>
                </>

            )
        }
    }
    glyphiconLogIn=()=>{
        if(this.state.log_in){
            return(
                <>
                <div style={{height:2,backgroundColor:'white',marginRight:50,marginLeft:50}}>                    
                </div>
                <div>
                    <span className="glyphicon glyphicon-menu-down"></span>
                </div>
                </>
            )
        }
    }
    customMargin=()=>{
        var margin="35%";
        var screenWidth= parseInt(window.innerWidth,10);
        if(screenWidth<=450){
            margin = "5%"
        }else{
            if(screenWidth<=600){
                margin="15%"
            }else{
                if(screenWidth<=800){
                    margin="20%"
                }
            }
        } 
        return margin
    }

    isLogIn=()=>{
         this.setState({
            log_in:true,
            create_Account:false,
            modalVisible:true,

        })
    } 

    isCreateAccount=()=>{
        this.setState({
            create_Account:true,
            log_in:false,
            modalVisible:true,

        })
    }

    closeModal=()=>{
        this.setState({
            create_Account:false,
            modalVisible:false,
            log_in:false,

        })
    }
    refreshNavBar=()=> {
        window.location.reload(false)
     }

     contentModal(){
        if(this.state.log_in){
            return(
                <div style={{marginTop:200}}>
                <h3 style={{textAlign:'center',color:'black',fontWeight:'bold',marginBottom:20}}>Se connecter</h3>



                        <div  className='buttonModal'>
                            <input type="submit" className="btn btn-primary form-control" value="Avec Google" />
                        </div>
                        <div  className='buttonModal'>
                            <input type="submit" className="btn btn-primary form-control" value="Avec Facebook" />
                        </div>
                        <div>
                            <p style={{textAlign:"center"}}>ou</p>
                        </div>



                    <form  onSubmit={this.isLoginAdmin} className="needs-validation">

                            <div  className='buttonModal'>
                                <input type='text' placeholder ='Nom' className ="form-control" name='Nom'
                                onChange={this.onChangeNom} required />
                            </div>
                            <div  className='buttonModal'>
                                <input type='password' placeholder ='Mot de passe' className ="form-control" name='Password'
                                onChange={this.onChangePassword} required />
                            </div>

                        <div  className='buttonModal'>
                            <input type="submit" className="btn btn-primary form-control" value="Se connecter" />
                        </div>
                    </form>
                </div>

            )
        }
        if(this.state.create_Account){
            return(
                <div style={{marginTop:180}}>
                <h3 style={{textAlign:'center',color:'black',fontWeight:'bold',marginBottom:20}}>Créer un compte utilisateur</h3>


                        <div className='buttonModal'>
                            <input type="submit" className="btn btn-primary form-control" value="Avec Google" />
                        </div>
                        <div className='buttonModal'>
                            <input type="submit" className="btn btn-primary form-control" value="Avec Facebook" />
                        </div>
                        <div >
                            <p style={{textAlign:"center"}}>ou</p>
                        </div>


                    <form onSubmit={this.isLoginClient} className="needs-validation">

                            <div className='buttonModal'>
                                <input type='email' placeholder ='Email' className ="form-control" name='email'
                                onChange={this.onChangeNom} required />
                            </div>
                            <div className='buttonModal'>
                                <input type='text' placeholder ='Nom' className ="form-control" name='Nom'
                                onChange={this.onChangeNom} required />
                            </div>
                            <div className='buttonModal'>
                                <input type='password' placeholder ='Mot de passe' className ="form-control" name='Password'
                                onChange={this.onChangePassword} required />
                            </div>
                       {this.InsideModalMargin}
                        <div className='buttonModal'>
                            <input type="submit" className="btn btn-primary form-control" value="S'abonner" />
                        </div>
                    </form>
                </div>

            )
        }
    }

    ShowModal(){
        return(
            <>       
            <div className='modal'>          
                <Modal isOpen={this.state.modalVisible} closeTimeoutMS={500}
                 contentLabel="modal" onRequestClose={this.closeModal} 
                    style={{
                        overlay: {
                        position: 'fixed',
                        top:0,
                        left: 0,
                        right: 0,
                        bottom:0,
                        backgroundColor: 'rgba(0,0,0,0.8)'
                        },
                        content: {
                        position: 'absolute',
                        top: '30px',                      
                        bottom:'20px',
                        height:'auto',
                        right:this.customMargin(),
                        left:this.customMargin(),
                        border: '0px solid rgba(0,0,0,0.8);',
                        background: 'white',
                        overflowX:'hidden',
                        WebkitOverflowScrolling: 'touch',
                        borderRadius: '10px',
                        outline: 'none',
                        padding: '0px'
                        }
                    }}>

                        <div className="column headerModal">

                            <div>
                                <span className="onlineModal" >OnlineSchool</span>
                                <p style={{position:"relative",fontSize:30,color:'rgba(0,0,0,0.8)'}} className="glyphicon glyphicon-remove"></p>
                            </div>
                            <div style={{padding:20}}>
                                <span style={{color:'white',textAlign:'center'}} >du texte et encore du texte encore et encore du texte</span>
                            </div>
                            <div className="row" style={{ fontWeight:"bold"}}>
                                <div style={{cursor:'pointer'}} onClick={this.isCreateAccount} className="col-xs-6">
                                    <div>
                                        <span style={{color:'white'}} >S'abonner</span>
                                    </div>
                                    <div>
                                        {this.glyphiconSignUp()}
                                    </div>
                                </div>
                                <div style={{cursor:'pointer'}} onClick={this.isLogIn} className="col-xs-6">
                                    <div>
                                        <span style={{color:'white'}} >Se connecter</span>
                                    </div>
                                    <div>
                                        {this.glyphiconLogIn()}
                                    </div>
                                </div>
                            </div>                            
                        </div>

                    {this.contentModal()}
                </Modal>

            </div>
            </>
        )
    }


    render(){
        return (
            <>

            <div className="jumbotron" style={{marginBottom:0,backgroundColor:'rgba(0,0,0,0.8)'}}>
            </div>
            <nav className="navbar navbar-expand-md navbar-default sticky" style={{marginBottom:0,minHeight:80}}>

                <button type="button" className="navbar-toggle iconStyle" data-toggle="collapse" data-target="#collapse_target">
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                </button>

                <a className="navbar-brand" href="/"><img src={logo} className="logo" /></a>                       

                <a href="/"><span id="onlineSchool" className="navbar-text">OnlineSchool</span></a>

                <NavLink exact to="/Search"><span className="glyphicon glyphicon-search navbar-brand" 
                    id="searchIcon"></span></NavLink>

                <div className="collapse navbar-collapse" id="collapse_target" style={{margin:0}}>  

                <ul className="nav navbar-nav navbar-right ul_nav" style={{ marginTop:10, marginRight:10}} >

                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink exact to="/">Accueil </NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink exact to="/Concours">Concours</NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink exact to="/Examens">Examens</NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink exact to="/contact">Nous contacter</NavLink>
                    </li>
                    <button onClick={this.isCreateAccount} className="button" style={{backgroundColor:'rgba(0,0,0,0.8)'}} >
                        <a className="navbar-brand" style={{color:'white',margin:"auto"}}>S'abonner</a>
                    </button>
                    <button onClick={this.isLogIn} className="button" style={{backgroundColor:"#660099",}} >
                        <a className="navbar-brand" style={{color:"white",margin:"auto"}}>Connexion</a>
                    </button>

                </ul>
                {this.ShowModal()}
                </div>                 
            </nav>

            </>
        );
    }
}

export default Navbar
