import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import logo from '../images/infographic.png';
import Modal from 'react-modal'

//#80D0D0
class Navbar extends Component {

    constructor(props){
        super(props)
        this.state={
            create_Account:false,
            log_in:false,
            modalVisible:false,
            showHeaderModal:false,

        }
    }

    headerModal=()=>{
        if(showHeaderModal){
            return(
                <div>
                    <span className="onlineModal">OnlineSchool</span>
                </div>
            )
        }       
    }
    customMargin=()=>{
        var margin="35%";
        var screenWidth= parseInt(window.innerWidth,10);
        if(screenWidth<=450){
            margin = "5%"
        }else{
            if(screenWidth<=600){
                margin="15%"
            }else{
                if(screenWidth<=800){
                    margin="20%"
                }
            }
        } 
        return margin
    }
    isLogIn=()=>{
         this.setState({
            log_in:!this.state.log_in,
            modalVisible:!this.state.modalVisible,
        })
    } 

    isCreateAccount=()=>{
        this.setState({
            create_Account:!this.state.create_Account,
            modalVisible:!this.state.modalVisible,
        })
    }

    closeModal=()=>{
        this.setState({
            create_Account:false,
            modalVisible:false,
            log_in:false,
        })
    }
    refreshNavBar=()=> {
        window.location.reload(false)
     }

     contentModal(){
        if(this.state.log_in){
            return(
                <div>
                <h3 style={{textAlign:'center',color:'black',fontWeight:'bold'}}>Connexion Admin</h3>
                    <form  onSubmit={this.isLoginAdmin} className="needs-validation">

                            <div className='col-md-12' style={{marginTop:50, marginLeft:'8%'}}>
                                <input type='text' placeholder ='Nom' className ="form-control" name='Nom'
                                onChange={this.onChangeNom} required />
                            </div>
                            <div className='col-md-12'style={{marginTop:20, marginLeft:'8%',marginBottom:50}}>
                                <input type='password' placeholder ='Mot de passe' className ="form-control" name='Password'
                                onChange={this.onChangePassword} required />
                            </div>

                        <div style={{marginLeft:'25%'}}>


                        <input type="submit" className="btn btn-primary" value="Se connecter" />
                            <button style={{marginLeft:10}} onClick={this.closeModal} 
                                className="btn btn-danger">Annuler</button>
                        </div>
                    </form>
                </div>

            )
        }
        if(this.state.create_Account){
            return(
                <div>
                <h3 style={{textAlign:'center',color:'black',fontWeight:'bold'}}>Connexion Client</h3>
                    <form onSubmit={this.isLoginClient} className="needs-validation">

                            <div className='col-md-12' style={{marginTop:50, marginLeft:'8%'}}>
                                <input type='text' placeholder ='Nom' className ="form-control" name='Nom'
                                onChange={this.onChangeNom} required />
                            </div>
                            <div className='col-md-12'style={{marginTop:20, marginLeft:'8%',marginBottom:50}}>
                                <input type='password' placeholder ='Mot de passe' className ="form-control" name='Password'
                                onChange={this.onChangePassword} required />
                            </div>

                        <div style={{marginLeft:'25%'}}>
                            <input type="submit" className="btn btn-primary" value="Se connecter" />
                            <button style={{marginLeft:10}} onClick={this.closeModal} 
                                className="btn btn-danger">Annuler</button>
                        </div>
                    </form>
                </div>

            )
        }
    }

    ShowModal(){
        return(
            <div className='modal'>
                <Modal isOpen={this.state.modalVisible} closeTimeoutMS={500}
                 contentLabel="modal" onRequestClose={this.closeModal} 
                    style={{
                        overlay: {
                        position: 'fixed',
                        top:0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        backgroundColor: 'rgba(0,0,0,0.8)'
                        },
                        content: {
                        position: 'absolute',
                        top: '180px',
                        width:'auto',
                        height:'auto',
                        bottom:'auto',
                        right:this.customMargin(),
                        left:this.customMargin(),
                        border: '1px solid black',
                        background: 'white',
                        overflow: 'auto',
                        WebkitOverflowScrolling: 'touch',
                        borderRadius: '10px',
                        outline: 'none',
                        padding: '0px'
                        }
                    }}>

                    {this.contentModal()}
                </Modal>

            </div>
        )
    }


    render(){
        return (
            <>

            <div className="jumbotron" style={{marginBottom:0}}>
            </div>
            <nav className="navbar navbar-expand-md navbar-default sticky" style={{marginBottom:0,minHeight:80}}>

                <button type="button" className="navbar-toggle iconStyle" data-toggle="collapse" data-target="#collapse_target">
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                    <span className="icon-bar"></span>
                </button>

                <a className="navbar-brand" href="/"><img src={logo} className="logo" /></a>                       

                <a href="/"><span id="onlineSchool" className="navbar-text">OnlineSchool</span></a>

                <NavLink to="/Search"><span className="glyphicon glyphicon-search navbar-brand" 
                    id="searchIcon"></span></NavLink>

                <div className="collapse navbar-collapse" id="collapse_target" style={{margin:0}}>  

                <ul className="nav navbar-nav navbar-right ul_nav" style={{ marginTop:10, marginRight:10}} >

                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink exact to="/">Accueil </NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink to="/Concours">Concours</NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink to="/Examens">Examens</NavLink>
                    </li>
                    <li className="li_nav" activeClassName="main-nav">
                        <NavLink to="/contact">Nous contacter</NavLink>
                    </li>
                    <button onClick={this.isCreateAccount} className="button" style={{backgroundColor:"gray"}} >
                        <a className="navbar-brand" style={{color:'white',margin:"auto"}}>S'abonner</a>
                    </button>
                    <button onClick={this.isLogIn} className="button" style={{backgroundColor:"#660099",}} >
                        <a className="navbar-brand" style={{color:"white",margin:"auto"}}>Connexion</a>
                    </button>

                </ul>
                {this.ShowModal()}
                </div>                 
            </nav>

            </>
        );
    }
}

export default Navbar
