import React, { Component } from 'react';
import '../user/dashboard.css'

export class dashboard extends Component {


    constructor(props){
        super(props)
        this.state={
            documents:[
                {
                    id:1,
                    categorie:'epreuve',
                    title:'Ecole normale supérieure',
                    date_registration:'12/02/2021'
                },
                {
                    id:2,
                    categorie:'correction',
                    title:'Ecole nationale supérieure polytechnique de Yaoundé 1',
                    date_registration:'12/02/2021'
                },
                {
                    id:3,
                    categorie:'epreuve',
                    title:'Facultés de médecine',
                    date_registration:'12/02/2021'
                }
            ],

        }
    }



    render(){

        const documents = this.state.documents
       

          const content = documents.map((document) =>
            
                <tr onClick={
                    ()=>this.setState({
                        categorie:document.categorie,
                        title:document.title,
                        date_registration:document.date_registration
                        })}>
                    <th scope="row">{document.categorie}</th>
                    <td>{document.title}</td>
                    <td>{document.date_registration}</td>
                </tr>
           
          );


        function showTable(){
            
            if(documents.length ===0){
                return(
                  <h5 style={{textAlign:'center',marginTop:100}}>
                      Aucun ticket dans cette rubrique</h5>
                )
            }
            if(documents.length <=8 && documents.length>=1){
              return (
                  <div className="container-fluid table">
            
                      <div className="table-wrapper-scroll-y my-custom-scrollbar">
                          <table className="table table-bordered table-hover mb-0">
                              <thead style={{backgroundColor:'orange'}}>
                                  <tr >
                                  <th scope="col">Catégorie</th>
                                  <th scope="col">Titre</th>
                                  <th scope="col">Inscription</th>
                                  </tr>
                              </thead>
                              <tbody>
                                  {content}
                              </tbody>
                          </table>
                      </div>
                  </div>
              )
              }
            if(documents.length>8){
              return (
                  <div className="container-fluid">
            
                      <div className="table-wrapper-scroll-y my-custom-scrollbar">
                          <table className="table table-bordered table-hover mb-0">
                              <thead style={{backgroundColor:'orange'}}>
                                  <tr >
                                  <th scope="col">#</th>
                                  <th scope="col">Titre</th>
                                  </tr>
                              </thead>
                              <tbody>
                                  {content}
                              </tbody>
                              <tfoot style={{backgroundColor:'orange'}}>
                                  <tr >
                                      <th scope="col">#</th>
                                      <th scope="col">Titre</th>
                                  </tr>
                              </tfoot>
                          </table>
                      </div>
                  </div>
              )
            }
          }

        return (
        <div >
            <h3 style={{margin:100}}>this is the dashboard page</h3>
            {showTable()}
            <div style={{height:100}}></div>
        </div>
        );
    }
}
